""".. module:: movienucleobase.py   :synopsis: Creates an excel file.. moduleauthor:: Pedro Araujo <pedroaraujo@colorlesscube.com>.. moduleauthor:: Pedro Nogueira <pedro.fig.nogueira@gmail.com>"""import numpy as npimport pandas as pddef build_excel(movie):    # Builds Excel with 5 columns    # 1 - Id    # 2 - Character name    # 3 - Character gender    # 4 - Number of scenes in which he appeared    # 5 - Character he interacted with    df_id_1, df_names, df_gender, df_n_scenes_real, df_scenes_real = movie.build_table_chars()    dataframeMovie = {                      'Id': pd.Series(df_id_1),                      'Name': pd.Series(df_names),                      'Gender': pd.Series(df_gender),                      'Number_Scene_Appearances': pd.Series(df_n_scenes_real),                      'Scene_Appearances': pd.Series(df_scenes_real),                      }    df = pd.DataFrame(dataframeMovie)    #Create a Pandas Excel writer    writer = pd.ExcelWriter('LOTR_1.xlsx', engine='xlsxwriter')    df.to_excel(writer, sheet_name='Sheet1')    writer.save()    # Builds Excel with 4 columns relative to interactions    # 1 - Character 1 Name    # 2 - Character 2 Name    # 1 - Character 1 Id    # 2 - Character 2 Id    # 3 - Number of Interactions    df_char_1, df_char_2, df_char_1_id, df_char_2_id, df_number = movie.build_table_interactions()    DataFrameMovie = {                      'Char_1': pd.Series(df_char_1),                      'Char_2': pd.Series(df_char_2),                      'Char_1_id': pd.Series(df_char_1_id),                      'Char_2_id': pd.Series(df_char_2_id),                      'Number_of_Interactions': pd.Series(df_number),                      }    df = pd.DataFrame(DataFrameMovie)    # Create a Pandas Excel writer    writer = pd.ExcelWriter('LOTR_2.xlsx', engine='xlsxwriter')    df.to_excel(writer, sheet_name='Sheet1')    writer.save()