""".. module:: movienucleobase.py   :synopsis: Creates excel files.. moduleauthor:: Pedro Araujo <pedroaraujo@colorlesscube.com>.. moduleauthor:: Pedro Nogueira <pedro.fig.nogueira@gmail.com>"""import numpy as npimport pandas as pddef build_excel_chars(movie):    # Builds Excel with 5 columns    # 1 - Id    # 2 - Character name    # 3 - Character gender    # 4 - Number of scenes in which he appeared    # 5 - Character he interacted with    df_id_1, df_names, df_gender, df_n_scenes_real, df_scenes_real = movie.build_table_chars()    dataframemovie = {                      'Id': pd.Series(df_id_1),                      'Name': pd.Series(df_names),                      'Gender': pd.Series(df_gender),                      'Number_Scene_Appearances': pd.Series(df_n_scenes_real),                      'Scene_Appearances': pd.Series(df_scenes_real),                      }    df = pd.DataFrame(dataframemovie)    #Create a Pandas Excel writer    writer = pd.ExcelWriter('Chars.xlsx', engine='xlsxwriter')    df.to_excel(writer, sheet_name='Sheet1')    writer.save()def build_excel_interactions(movie):    # Builds Excel with 5 columns relative to interactions    # 1 - Character 1 Name    # 2 - Character 2 Name    # 3 - Character 1 Id    # 4 - Character 2 Id    # 5 - Number of Interactions    df_char_1, df_char_2, df_char_1_id, df_char_2_id, df_number = movie.build_table_interactions()    dataframemovie = {                      'Char_1': pd.Series(df_char_1),                      'Char_2': pd.Series(df_char_2),                      'Char_1_id': pd.Series(df_char_1_id),                      'Char_2_id': pd.Series(df_char_2_id),                      'Number_of_Interactions': pd.Series(df_number),                      }    df = pd.DataFrame(dataframemovie)    # Create a Pandas Excel writer    writer = pd.ExcelWriter('Interactions.xlsx', engine='xlsxwriter')    df.to_excel(writer, sheet_name='Sheet1')    writer.save()def build_excel_mentions(movie):    # Builds Excel with 5 columns relative to mentions    # 1 - Character 1 Name    # 2 - Character 2 Name    # 3 - Character 1 Id    # 4 - Character 2 Id    # 5 - Number of Interactions    df_char_1, df_char_2, df_char_1_id, df_char_2_id, df_number = movie.build_table_mentions()    dataframemovie = {                      'Char_1': pd.Series(df_char_1),                      'Char_2': pd.Series(df_char_2),                      'Char_1_id': pd.Series(df_char_1_id),                      'Char_2_id': pd.Series(df_char_2_id),                      'Number_of_Mentions': pd.Series(df_number),                      }    df = pd.DataFrame(dataframemovie)    # Create a Pandas Excel writer    writer = pd.ExcelWriter('Mentions.xlsx', engine='xlsxwriter')    df.to_excel(writer, sheet_name='Sheet1')    writer.save()